mapper.insert:
INSERT INTO tb_token3 (
                        user_type
                      , user_no
                      , random_access_token
                      , access_expire_dt
                      , random_refresh_token
                      , refresh_expire_dt
                      , reg_dt
                      , upd_dt
) VALUES (
           #{userType}
         , #{userNo}
         , #{randomAccessToken}
         , #{accessExpireDt}
         , #{randomRefreshToken}
         , #{refreshExpireDt}
         , now()
         , now()
         )

mapper.update:
UPDATE tb_token3
SET
    random_access_token = #{randomAccessToken}
  , access_expire_dt = #{accessExpireDt}
  , random_refresh_token = #{randomRefreshToken}
  , refresh_expire_dt = #{refreshExpireDt}
  , upd_dt = now()
WHERE token_no = #{tokenNo}
