mapper.insert:
INSERT INTO tb_item (
                        admin_no
                      , category_no
                      , name
                      , small_image
                      , big_image
                      , price
                      , intro
                      , content
                      , remains
                      , fast_yn
                      , reg_dt
                      , upd_dt
) VALUES (
             #{adminNo}
         , #{categoryNo}
         , #{name}
         , #{smallImage}
         , #{bigImage}
         , #{price}
         , #{intro}
         , #{content}
         , #{remains}
         , #{fastYn}
         , now()
         , now()
         )

    SELECT LAST_INSERT_ID()

mapper.select:
SELECT item.item_no , item.admin_no , item.name , item.status , item.price , item.remains , item.fast_yn , item.ad_yn , item.md_recommend_yn , item.reg_dt FROM tb_item as item JOIN (SELECT item_no FROM tb_item WHERE 1=1
AND admin_no = #{adminSeq}
AND item_no >= #{pageRequest.startPage}
ORDER BY item_no DESC LIMIT #{pageRequest.pageSize} ) as idx on item.item_no = idx.item_no

mapper.update:
UPDATE tb_item
SET name = #{name}
, remains = #{remains}
, price = #{price}
, intro = #{intro}
, big_image = #{bigImage}
, small_image = #{smallImage}
, content = #{content}
, ad_yn = #{adYn}
, md_recommend_yn = #{mdRecommendYn}
, fast_yn = #{fastYn}
, upd_dt = now()
WHERE 1=1
  AND item_no = #{itemSeq}
